section.programlisting:
#include <iostream>
#include <limits>

int main(){
    using namespace std;
    float a = numeric_limits<float>::max();
    float b = a + numeric_limits<float>::max();
    cout << "b is " << b << endl;

    return 0;
}

//no underflow example

safe_float<double, check_overflow, on_fail_throw> a = 1;

safe_float<double, check_overflow, on_fail_throw> b = numeric_limits<double>::max();

safe_float<double, check_overflow, on_fail_throw> c = a / b ; //throws because of underflow.

//no rounding example

safe_float<double, check_inexact_rounding, abort_on_fail> d = 0.3_sd; // compilation error, the number is not
representable.

safe_float<double, check_inexact_rounding, abort_on_fail> e = 2E25;

safe_float<double, check_inexact_rounding, abort_on_fail> f = 2E100;

safe_float<double, check_inexact_rounding, abort_on_fail> g = e + f ; //aborts execution with runtime error.

//multiple conditions example

template<typename FP>
using wanted_check = combine_check<check_overflow, check_inexact_rounding>;

safe_float<double, wanted_check> h = numeric_limits<double>::max();

safe_float<double, wanted_check> i = numeric_limits<double>::max();

safe_float<double, wanted_check> j = h + i; // throws because of
overflow to infinity.

#include <iostream>
#include <limits>

int main(){
    using namespace std;
    float a = numeric_limits<float>::max();
    float b = a + numeric_limits<float>::max();
    cout << "b is " << b << endl; //outputs: b is inf

    return 0;
}

#include <iostream>
#include <limits>
#include <boost/safe_float.hpp>

int main(){
    using namespace std;
    using namespace boost::safe_float;
    try {
        using sf_without_overflow = safe_float<float,policy::check_addition_overflow>;
        sf_without_overflow a = numeric_limits<sf_without_overflow >::max();
        sf_without_overflow b = a + a;
    } catch ( const boost::safe_float_exception& e) {
        cout << "safe_float required guarantees broken" << endl;
    }

    return 0;
}

para.programlisting:
#include <iostream>
#include <limits>
#include <boost/safe_float.hpp>

int main(){
    using namespace std;
    using namespace boost::safe_float;
    using sf = safe_float<float,
        policy::check_overflow,
        policy::on_fail_throw,
        policy::allow_cast_from<int>
    >;
    int i = 1;
    double d = 2.0;
    sf a{i}; //works
    sf b{d}; //fails compilation

    return 0;
}

section.para:
The following is a example of how to use the library for asserting
      in place of throwing.
      #include <iostream>
#include <limits>
#include <boost/safe_float.hpp>

int main(){
    using namespace std;
    using namespace boost::safe_float;
    using sf_asserting_overflows=safe_float<float,policy::check_overflow, policy::on_fail_assert>;
    sf_asserting_overflows a = numeric_limits<sf_asserting_overflows>::max();
    sf_asserting_overflows b = a + a;

    return 0;
}

section.programlisting:
template<typename FP>
struct Check {
    // Example of a valid CHECK policy that would detect result equals to 0 as errors

    // Default connstructible

    bool pre_addition_check(FP a, FP B) {
        return true;
    }
    bool post_addition_check(FP r) {
        return r != FP(0);
    }

    bool pre_subtraction_check(FP a, FP B) {
        return true;
    }
    bool post_subtraction_check(FP r) {
        return r != FP(0);
    }

    bool pre_multiplication_check(FP a, FP B) {
        return true;
    }
    bool post_multiplication_check(FP r) {
        return r != FP(0);
    }

    bool pre_division_check(FP a, FP B) {
        return true;
    }
    bool post_division_check(FP r) {
        return r != FP(0);
    }
};

struct Report {
    // Example of a valid REPORT policy that would throw exceptions on error

    // Default constructible

    bool report_failure(std::string const& e) {
        throw std::runtime_error(e);
    }
};

template<typename SF>
struct Cast {
    // Example of a valid CAST policy that allow implicit construction from floating point numbers
    // and explicit conversion to floating point numbers

    template<typename T>
    static constexpr bool can_cast_from = std::is_floating_point<T>::value;

    template<typename T>
    static constexpr bool can_explicitly_cast_from = false;

    template<typename T>
    static void cast_from(SF::value_type& f, T t) {
        f = t; 
    }


    template<typename T>
    static constexpr bool can_cast_to = false;

    template<typename T>
    static constexpr bool can_explicitly_cast_to = std::is_floating_point<T>::value;

    template<typename T>
    static T cast_to(SF::value_type f) {
        return T{f};
    }
};

section.para:
For combining the Policies a composer function is used called
          policy::compose_check, the function receives variadic template list of policies
          and returns a new policy type. It is being used as such:

template<typename FP>
using new_policy = policy::compose_check<FP, policy::check_overflow, /* arbitrary checks */>;

safe_float<float, new_policy> sf;
